    await driver.findElement(By.id("text")).click()
    await driver.findElement(By.id("text")).sendKeys("Doctor")
    await driver.findElement(By.css("center:nth-child(5)")).click()
    await driver.findElement(By.css(".btn-outline-secondary")).click()
